document.getElementById('entry').onclick = function () { let login = document.getElementById('login').value; let password = document.getElementById('password').value; let out = document.getElementById('out'); if (login === 'Дима' && password === 'Дима' || login === 'Дима' && password === '777' || login === 'Чер' && password === 'b5678') { out.innerHTML = ''; } else { out.innerHTML = 'Пользователя нет в этой системе "спросите у администратора"'; } }
